import base64, os

files = {
    "src/app/api/coins/route.ts": "",
    "src/app/api/screen/route.ts": "",
}

for path, b64 in files.items():
    os.makedirs(os.path.dirname(path), exist_ok=True)
    with open(path, 'w') as f:
        f.write(base64.b64decode(b64).decode('utf-8'))
    print(f"âœ“ {path}")

print("\nDone! Run: npm run build")
